import { useEffect, useState } from 'react'
import './highlight.css'
import { highlight, languages } from 'prismjs/components/prism-core'
import 'prismjs/components/prism-clike'
import 'prismjs/components/prism-markup'
import 'prismjs/components/prism-javascript'
import 'prismjs/components/prism-jsx'

const jsxCode =
  "import { Checkbox } from 'antd';\n" +
  '\n' +
  'function onChange(e) {\n' +
  '  console.log(`checked1133 = ${e.target.checked}`);\n' +
  '}\n' +
  '\n' +
  'ReactDOM.render(<Checkbox onChange={onChange}>Checkbox</Checkbox>, mountNode);'

const HighlightCode = () => {
  const [highlightCodes, setHighlightCodes] = useState<string>('')
  const getHighlightCode = () => {
    const res = highlight(jsxCode, languages.jsx, 'jsx')
    setHighlightCodes(res)
  }
  useEffect(() => {
    getHighlightCode()
  }, [])
  return (
    <div>
      <pre className="language-jsx">
        <code dangerouslySetInnerHTML={{ __html: highlightCodes }} />
      </pre>
    </div>
  )
}

export default HighlightCode

md2htmlindex:
import React, { useState } from 'react'
import './index.css'
import { marked } from 'marked'

const Md2Html = () => {
  const [html, setHtml] = useState<string>('')
  const md2html = (mdData: string) => {
    return marked(mdData, {
      renderer: new marked.Renderer(),
      gfm: true,
      breaks: false,
    })
  }
  const onTextareaChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setHtml(md2html(e.target.value))
  }
  return (
    <div className="md2html">
      <div className="left block">
        <textarea name="" id="textarea" onChange={onTextareaChange} placeholder="请输入md" />
      </div>
      <div className="right block" dangerouslySetInnerHTML={{ __html: html }} />
    </div>
  )
}

export default Md2Html

